const clientsCarousel = document.getElementById('clients-carousel');

function Carousel() {
  const scrollAmount = clientsCarousel.scrollLeft + clientsCarousel.offsetWidth;
  clientsCarousel.scrollTo({
    left: scrollAmount,
    behavior: 'smooth'
  });
}

setInterval(Carousel, 3000); // Cambia el valor 3000 para ajustar la velocidad de desplazamiento

export default Carousel

ReactDOM.render(<Carousel />, document.getElementById('rootCarousel'));
